// The Vue build version to load with the `import` command
// (runtime-only or standalone) has been set in webpack.base.conf with an alias.
import Vue from "vue";
import App from "./App";
import API from "./api";
import router from "./router";
// FIXME can't have single store for multiple instances to be generated by succeeding invocations of generator exposed below
import store from "./store";

Vue.config.productionTip = false;

API.expose( ( element, options = {} ) => new Vue( {
	el: element,
	router,
	store,
	components: { App },
	template: "<App/>",
	form: options,
} ) );
